<div class="d1">
    <h2>404 page</h2>
    <div class="col-md-12">
        <div class="col-md-6 p-t-34">
            <section class="bty-page-error-1">
                <div>
                    <p>404<br>
                        <small>PAGE NOT FOUND</small>
                    </p>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
            </section>
        </div>
        <div class="col-md-6">
            <h5>bty-page-error-1</h5>
            <textarea class="form-control" name="" id="" cols="30" rows="5" readonly>
                .bty-page-error-1 >div {
    background:#fff;
    text-align: center;
    position: relative;
}

.bty-page-error-1 >div p {
    font-size: 240px;
    color: #fff;
    padding-top: 60px;
    position: relative;
    z-index: 9;
    line-height: 100%;
}

.bty-page-error-1 >div p small {
    font-size: 40px;
    line-height: 100%;
    vertical-align: top;
}

.bty-page-error-1 >div span:nth-of-type(3) {
    width: 140px;
    height: 140px;
    border-radius: 50%;
    background: #48A9E6;
    position: absolute;
    z-index: 1;
    top: 80px;
    left: 50%;
    animation: 7s small-bty-page-error-1 infinite cubic-bezier(1,.22,.71,.98);
    -webkit-animation: 7s small-bty-page-error-1 infinite cubic-bezier(1,.22,.71,.98);
    animation-delay: 1.2s;
    -webkit-animation-delay: 1.2s;
}

.bty-page-error-1 >div span:nth-of-type(2) {
    width: 200px;
    height: 200px;
    border-radius: 50%;
    background: #48A9E6;
    position: absolute;
    z-index: 1;
    top: 0;
    left: 10%;
    animation: 7s med-bty-page-error-1 infinite cubic-bezier(.32,.04,.15,.75);
    -webkit-animation: 7s med-bty-page-error-1 infinite cubic-bezier(.32,.04,.15,.75);
    animation-delay: 0.4s;
    -webkit-animation-delay: 0.4s;
}

.bty-page-error-1 >div span:nth-of-type(1) {
    width: 400px;
    height: 400px;
    border-radius: 50%;
    background: #48A9E6;
    position: absolute;
    z-index: 1;
    top: 200px;
    right: 0;
    animation: 8s big-bty-page-error-1 infinite;
    -webkit-animation: 8s big-bty-page-error-1 infinite;
    animation-delay: 3s;
    -webkit-animation-delay: 1s;
}

@-webkit-keyframes small-bty-page-error-1 {
    0% { top: 10px; left: 45%; opacity: 1; }
    25% { top: 300px; left: 40%; opacity:0.7; }
    50% { top: 240px; left: 55%; opacity:0.4; }
    75% { top: 100px; left: 40%;  opacity:0.6; }
    100% { top: 10px; left: 45%; opacity: 1; }
}
@keyframes small-bty-page-error-1 {
    0% { top: 10px; left: 45%; opacity: 1; }
    25% { top: 300px; left: 40%; opacity:0.7; }
    50% { top: 240px; left: 55%; opacity:0.4; }
    75% { top: 100px; left: 40%;  opacity:0.6; }
    100% { top: 10px; left: 45%; opacity: 1; }
}

@-webkit-keyframes med-bty-page-error-1 {
    0% { top: 0; left: 20%; opacity: 1; }
    25% { top: 300px; left: 80%; opacity:0.7; }
    50% { top: 240px; left: 55%; opacity:0.4; }
    75% { top: 100px; left: 40%;  opacity:0.6; }
    100% { top: 0; left: 20%; opacity: 1; }
}

@keyframes med-bty-page-error-1 {
    0% { top: 0; left: 20%; opacity: 1; }
    25% { top: 300px; left: 80%; opacity:0.7; }
    50% { top: 240px; left: 55%; opacity:0.4; }
    75% { top: 100px; left: 40%;  opacity:0.6; }
    100% { top: 0; left: 20%; opacity: 1; }
}

@-webkit-keyframes big-bty-page-error-1 {
    0% { top: 0; right: 4%; opacity: 0.5; }
    25% { top: 100px; right: 40%; opacity:0.4; }
    50% { top: 240px; right: 45%; opacity:0.8; }
    75% { top: 100px; right: 35%;  opacity:0.6; }
    100% { top: 0; right: 4%; opacity: 0.5; }
}
@keyframes big-bty-page-error-1 {
    0% { top: 0; right: 4%; opacity: 0.5; }
    25% { top: 100px; right: 40%; opacity:0.4; }
    50% { top: 240px; right: 45%; opacity:0.8; }
    75% { top: 100px; right: 35%;  opacity:0.6; }
    100% { top: 0; right: 4%; opacity: 0.5; }
}
