<div class="d1">
    <h2>Input Text</h2>
    <div class="col-md-12">
        <div class="col-md-6 p-t-34">
            <input class="bty-input-text-1" type="text" placeholder="name">
        </div>
        <div class="col-md-6">
            <h5>bty-input-text-1</h5>
            <textarea class="form-control" name="" id="" cols="30" rows="5" readonly>
                .bty-input-text-1 {
    font-size: 14px;
    padding: 10px 8px 10px 8px;
    border-radius: 5px;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, .55), 0px 1px 1px rgba(255, 255, 255, .5);
    opacity: 0.5;
    border: 1px solid #666;
}

.bty-input-text-1:hover,
.bty-input-text-1:focus {
    opacity: .7;
    color: #08c;
    border: 1px solid #08c;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, .25), inset 0px 3px 6px rgba(0, 0, 0, .25);
}

.bty-input-text-1:focus {
    box-shadow: inset 0 1px 2px rgba(255, 255, 255, .35), 0px 1px 15px rgba(0, 246, 255, .5);
    border: 1px solid #08c;
    outline: none;
}
            </textarea>
        </div>
    </div>
