                .bty-notification-1 {
    background-color: white;
}
.bty-notification-1>ul{
    list-style: none;
    padding: 0;
    margin: 0;
}
.bty-notification-1>ul li{
    border-bottom: 1px solid rgba(120, 130, 140, 0.13);
    display: block;
    text-decoration: none;
    padding: 9px 15px;
}
.bty-notification-1>ul li>div:nth-of-type(1){
    position: relative;
    display: inline-block;
    margin: 0 10px 15px 0;
    width: 45px;
    white-space: normal;
}
.bty-notification-1>ul li>div:nth-of-type(2){
    white-space: normal;
    display: inline-block;
    width: 75%;
    vertical-align: middle;
}
.bty-notification-1>ul li>div:nth-of-type(1) >span{
    line-height: 48px;
    color: #ffffff;
    width: 45px;
    height: 45px;
    display: inline-block;
    font-weight: 400;
    text-align: center;
    border-radius: 100%;
    /*background: #1e88e5;*/
}
.bty-notification-1>ul li:nth-of-type(odd)>div:nth-of-type(1) >span{
    background: #1e88e5;
}
.bty-notification-1>ul li:nth-of-type(even)>div:nth-of-type(1) >span{
    background: #fc4b6c;
}
.bty-notification-1>ul li>div:nth-of-type(2) h6{
    margin: 5px 0 0;
    line-height: 18px;
    font-size: 16px;
    font-weight: 400;
}
.bty-notification-1>ul li>div:nth-of-type(2) span{
    font-size: 12px;
    display: block;
    margin: 1px 0;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    overflow: hidden;
    color: #67757c;
    white-space: nowrap;
}
.bty-notification-1>ul li:hover{
    background-color: rgba(238, 238, 238, 0.75);
}
