.bty-our-team-blur-3{
    z-index: 0;
    position: relative;
    box-sizing: border-box;
}

.bty-our-team-blur-3 > div {
    background: #000;
    text-align: center;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.3);
    overflow: hidden;
    position: relative;
}

.bty-our-team-blur-3 > div div:nth-of-type(1) img {
    width: 100%;
    height: 280px;
    object-fit: cover;
}

.bty-our-team-blur-3 > div div:nth-of-type(2) {
    padding: 5px 10px 10px;
    position: absolute;
    top: 50%;
    left: 50%;
    opacity: 0;
    transform: rotate(0deg) translate(-50%, -50%);
    transform-origin: 0 0;
    transition: all 0.55s ease 0s;
}

.bty-our-team-blur-3 > div div:nth-of-type(2):before, .bty-our-team-blur-3 > div div:nth-of-type(2):after {
    content: "";
    width: 400px;
    height: 2px;
    background: #95ccea;
    position: absolute;
    top: 0;
    left: 10px;
    transform: translateX(100%);
    transition: all 0.55s ease 0s;
}

.bty-our-team-blur-3 > div div:nth-of-type(2):after {
    top: auto;
    left: auto;
    bottom: 0;
    right: 10px;
    transform: translateX(-100%);
}

.bty-our-team-blur-3 > div div:nth-of-type(2) h4 {
    font-size: 20px;
    font-weight: 400;
    color: #fff;
    text-transform: uppercase;
