    <h2>Pagination</h2>
    <div class="col-md-12">
        <div class="col-md-6 p-t-34">
            <div class="bty-pagination-1">
                <ul>
                    <li><a href="#"></a></li>
                    <li><a href="#"></a></li>
                    <li><a href="#">1</a></li>
                    <li><a href="#">2</a></li>
                    <li class="active"><a href="#">3</a></li>
                    <li><a href="#">4</a></li>
                    <li><a href="#">5</a></li>
                    <li><a href="#"></a></li>
                    <li><a href="#"></a></li>
                </ul>
            </div>
        </div>
        <div class="col-md-6">
            <h5>bty-pagination-1</h5>
            <textarea class="form-control" name="" id="" cols="30" rows="5" readonly>
                .bty-pagination-1 {
    text-align: center;
}

.bty-pagination-1 > ul li {
    list-style-type: none;
    display: inline-block;
}

.bty-pagination-1 > ul li > a:hover,
.bty-pagination-1 > ul li > a.active {
    border-bottom: 3px solid #499bc7;
    color: #666;
    font-weight: bold;
}

.bty-pagination-1 > ul li > a {
    padding: 0.8em 1em;
    display: inline-block;
    text-decoration: none;
    color: #499bc7;
    border-bottom: 3px solid #d7dadb;
    font-size: 16px;
}

.bty-pagination-1 > ul li:first-child > a::before {
    font-family: 'FontAwesome';
    content: "\f100";
}

.bty-pagination-1 > ul li:nth-of-type(2) > a::before {
    font-family: 'FontAwesome';
    content: "\f104";
}

.bty-pagination-1 > ul li:nth-last-child(2) > a::after {
    font-family: 'FontAwesome';
    content: "\f105";
}

.bty-pagination-1 > ul li:last-child > a::after {
    font-family: 'FontAwesome';
    content: "\f101";
}
            </textarea>
        </div>
    </div>
