<div class="d1">
    <h2>Range</h2>
    <div class="col-md-12">
        <div class="col-md-6 p-t-34">
            <input type="range" class="bty-input-range-1">
        </div>
        <div class="col-md-6">
            <h5>bty-input-range-1</h5>
            <textarea class="form-control" name="" id="" cols="30" rows="5" readonly>
                .bty-input-range-1::-webkit-slider-thumb {
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
}

.bty-input-range-1 {
    -webkit-appearance: none;
    width: 50%;
    background-color: transparent;
}

.bty-input-range-1:focus {
    outline: 0;
}

.bty-input-range-1::-webkit-slider-thumb {
    -webkit-appearance: none;
    height: 45px;
    width: 45px;
    border-radius: 50%;
    background: white;
    border: 10px solid rgba(0, 0, 0, 0.2);
    background-clip: padding-box;
    cursor: pointer;
    margin-top: -13px;
}

.bty-input-range-1::-webkit-slider-runnable-track {
    width: 100%;
    height: 20px;
    border-top: 2px solid black;
    border-bottom: 1px solid #cc99f0;
    cursor: pointer;
    background: -webkit-linear-gradient(left, #00dbde 10%, #fc00ff 90%);
    background: linear-gradient(90deg, #00dbde 10%, #fc00ff 90%);
    border-radius: 50px;
}
            </textarea>
        </div>
    </div>
