<div class="d1">
    <h2>Textarea</h2>
    <div class="col-md-12">
        <div class="col-md-6 p-t-34">
            <textarea placeholder="This is an placeholder box" class="bty-textarea-1"></textarea>
        </div>
        <div class="col-md-6">
            <h5>bty-textarea-1</h5>
            <textarea name="" id="" cols="35" rows="2" readonly=""
                      style="margin-top: 0; margin-bottom: 0; height: 100px; width: 100%">
              .bty-textarea-1 {
    width: 100%;
    height: 100px;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    -moz-border-right-colors: none;
    -moz-border-top-colors: none;
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.07);
    border-color: -moz-use-text-color #FFFFFF #FFFFFF -moz-use-text-color;
    border-image: none;
    border-radius: 6px 6px 6px 6px;
    border-style: none solid solid none;
    border-width: medium 1px 1px medium;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.12) inset;
    color: #555555;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 1em;
    line-height: 1.4em;
    padding: 5px 8px;
    transition: background-color 0.2s ease 0s;
}

.bty-textarea-1:focus {
    background: none repeat scroll 0 0 #FFFFFF;
    outline-width: 0;
}
    </textarea>
        </div>

    </div>
    <div class="col-md-12">
        <div class="col-md-6 p-t-34">
            <textarea placeholder="This is an placeholder box" class="bty-textarea-2"></textarea>
        </div>
        <div class="col-md-6">
            <h5>bty-textarea-2</h5>
            <textarea name="" id="" cols="35" rows="2" readonly=""
                      style="height: 100px; width: 100%">
.bty-textarea-2 {
    width: 100%;
    line-height: 1.308em;
    padding: 10px;
    min-height: 120px;
    resize: vertical;
    outline: none;
    background: #499bc7;
    border: none;
    margin-bottom: 20px;
    box-shadow: 3px 3px 6px rgba(0, 0, 0, .5) inset;
    -webkit-box-shadow: 3px 3px 6px rgba(0, 0, 0, .5) inset;
    color: #fff;
    font-size: 0.813em;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    transition: all .2s;
    -webkit-transition: all .2s;
}

.bty-textarea-2:focus {
    background-color: #4879a5;
}

.bty-textarea-2::-webkit-input-placeholder {
    color: #fff;
    font-style: italic;
}

.bty-textarea-2:-moz-placeholder {
    color: #fff;
    font-style: italic;
}
            </textarea>
        </div>
    </div>
    <div class="col-md-12">
        <h4>Textarea 3</h4>
        <h5>bty-textarea-3</h5>
        <textarea placeholder="This is an placeholder box" class="bty-textarea-3"></textarea>
    </div>

</div>