               .bty-timeline-1{
    border-collapse: collapse;
    border-spacing: 0;
    display: table;
    position: relative;
    table-layout: fixed;
    width: 100%;
    padding: 20px;
}
.bty-timeline-1:before{
    background-color: #C7CBD6;
    bottom: 0;
    content: "";
    left: 50%;
    position: absolute;
    top: 0;
    width: 2px;
    z-index: 0;
}
.bty-timeline-1 article{
    display: table-row;
}
.bty-timeline-1 article:before, .bty-timeline-1 article:after{
    content: "";
    display: block;
    width: 50%;
}
.bty-timeline-1 article>div{
    /*display: table-cell;*/
    vertical-align: top;
    width: 50%;
}
.bty-timeline-1 article>div>div{
    display: block;
    margin-left: 25px;
    position: relative;
    text-align: left;
    background: #F4F4F4;
    margin-bottom: 5px;
    border-radius: 4px;
}
.bty-timeline-1 article>div>div>div{
    padding: 15px;
}
.bty-timeline-1 article>div>div>div:before, .bty-timeline-1 article>div>div>div:after{
    display: table;
    content: " ";
    clear: both;
}
.bty-timeline-1 article>div>div>div span:nth-of-type(1){
    border-bottom: 8px solid transparent;
    border-top: 8px solid transparent;
    display: block;
    height: 0;
    left: -7px;
    position: absolute;
    top: 13px;
    width: 0;
    border-right: 8px solid #F4F4F4 !important;
}
.bty-timeline-1 article>div>div>div span:nth-of-type(2){
    background: #EF6F66;
    box-shadow: 0 0 0 3px #EF6F66;
    border: 3px solid #FFFFFF;
    border-radius: 50%;
    -webkit-border-radius: 50%;
    display: block;
    height: 12px;
    width: 12px;
    left: -30px;
    position: absolute;
    top: 15px;
}
.bty-timeline-1 article>div>div>div span:nth-of-type(3){
    left: -200px;
    position: absolute;
    text-align: right;
    top: 12px;
    width: 150px;
}
.bty-timeline-1 article>div>div>div h6{
    font-size: 16px;
    font-weight: 300;
    margin: 0 0 5px;
    color: #EF6F66;
}
.bty-timeline-1 article>div>div>div p{
    font-size: 12px;
    margin-bottom: 0;
}
.bty-timeline-1 article:nth-of-type(odd)>div{
    float: right;
}
.bty-timeline-1 article:nth-of-type(even)>div>div{
    margin-left: 0;
    margin-right: 25px;
}
.bty-timeline-1 article:nth-of-type(even)>div>div>div span:nth-of-type(1){
    border-bottom: 8px solid transparent;
    border-top: 8px solid transparent;
    display: block;
    height: 0;
    right: -7px;
    position: absolute;
    top: 13px;
    width: 0;
    left: auto;
    border-left: 8px solid #F4F4F4 !important;
    border-right: 0 !important;
}
.bty-timeline-1 article:nth-of-type(even)>div>div>div span:nth-of-type(2){
    left: auto;
    right: -32px;
    background: #39B6AE;
    box-shadow: 0 0 0 3px #39B6AE;
}
.bty-timeline-1 article:nth-of-type(even)>div>div>div span:nth-of-type(3){
    left: auto;
    right: -200px;
    text-align: left;
}
.bty-timeline-1 article:nth-of-type(even)>div>div>div h6{
    color: #39B6AE;
}
