            .bty-todo-list-1{
    background-color: white;
    width: 100%;
}
.bty-todo-list-1>ul{
    margin: 0;
    padding: 0;
    list-style: none;
}
.bty-todo-list-1>ul a{
    text-decoration: none;
}
.bty-todo-list-1>ul li{
    border-bottom: 1px solid #ddd;
}
.bty-todo-list-1>ul >li>a{
    display: block;
    color: #333;
}
.bty-todo-list-1>ul li a>div:first-of-type{
    text-align: center;
    font-size: 14px;
    border-right: 1px solid #ddd;
    padding-left: 15px;
}
.bty-todo-list-1>ul li a>div:first-of-type,.bty-todo-list-1>ul li a>div:last-of-type {
    display: table-cell;
    padding: 10px;
}
.bty-todo-list-1>ul li a>div:first-of-type i{
    display: block;
    width: 14px;
    text-align: center;
}
.bty-todo-list-1>ul li a>div:last-of-type{
    padding-right: 15px;
}
.bty-todo-list-1>ul>li>a:focus, .bty-todo-list-1>ul>li>a:hover {
    background: #fafafa;
    text-decoration: none;
}
.bty-todo-list-1>ul>li>a:focus div:first-of-type i:before,
.bty-todo-list-1>ul>li>a:hover div:first-of-type i:before{
    content: '\f14a';
    opacity: .4;
    filter: alpha(opacity=40);
    color: #2d353c;
}
