<form class="form-horizontal">
    <fieldset>

        <!-- Form Name -->
        <legend>Form Name</legend>

        <!-- Text input-->
        <div class="form-group">
            <label class="col-md-4 control-label" for="client_id">Client ID</label>
            <div class="col-md-4">
                <input id="client_id" name="client_id" type="text" placeholder="" class="form-control input-md">

            </div>
        </div>

        <!-- Text input-->
        <div class="form-group">
            <label class="col-md-4 control-label" for="client_secret">Client Secret</label>
            <div class="col-md-4">
                <input id="client_secret" name="client_secret" type="text" placeholder="" class="form-control input-md">

            </div>
        </div>

        <!-- Text input-->
        <div class="form-group">
            <label class="col-md-4 control-label" for="callbackurl">Call Back Url</label>
            <div class="col-md-4">
                <input id="callbackurl" type="text" readonly class="form-control input-md">
                <span class="help-block">Use this url to create App</span>
            </div>
        </div>

        <!-- Button -->
        <div class="form-group">
            <label class="col-md-4 control-label" for=""></label>
            <div class="col-md-4">
                <button id="" name="" class="btn btn-primary">Save</button>
            </div>
        </div>

    </fieldset>
</form>
